mapper.select:
SELECT id,name,url,level,parent,icon,remarks,date_format(c_time,'%Y/%m/%d %H:%i:%s') as c_time,c_user,seq FROM sys_menu


        and id = #{id}


        and name like "%"#{name}"%"


        and url like "%"#{url}"%"


        and level = #{level}


        and parent = #{parent}


        and icon like "%"#{icon}"%"


        and remarks like "%"#{remarks}"%"


        and c_time >= #{c_time_0}


        and c_time <= #{c_time_1}


        and c_user = #{c_user}


        and seq = #{seq}


ORDER BY ${_order_}
